/* Procedure structure for procedure `GdeleteById` */

/*!50003 DROP PROCEDURE IF EXISTS  `GdeleteById` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `GdeleteById`(IN myID varchar(32))
BEGIN
	update goods set del_flag='1' where id= myID;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `GinsertById` */

/*!50003 DROP PROCEDURE IF EXISTS  `GinsertById` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `GinsertById`(IN id varchar (32),IN name_String VARCHAR(20), IN price_decimal decimal(10,2), 
					IN origin_String VARCHAR(20),IN stock_double  DECIMAL(10,2),IN warehouse_id  VARCHAR (32),
					IN category_id  VARCHAR (32))
BEGIN
	insert into goods(id,name,price,origin,stock,warehouse_id,category_id,del_flag)  
	values(id,name_String,price_decimal,origin_String,stock_double,warehouse_id,category_id,'0');
    END */$$
DELIMITER ;

/* Procedure structure for procedure `GselectAll` */

/*!50003 DROP PROCEDURE IF EXISTS  `GselectAll` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `GselectAll`()
BEGIN
	select id,name from goods where 1=1 and del_flag='0' ;
    END */$$
DELIMITER ;

